function createSnow(){let e=document.querySelector(".container"),t=document.createElement("span");t.style.left=Math.random()*innerWidth+"px",e.appendChild(t),setTimeout(()=>{t.remove()},5e3)}setInterval(createSnow,100);let counteDate=new Date("December 31, 2021 00:00:00").getTime();function countDown(){let e=(new Date).getTime();gap=counteDate-e;let t=Math.floor(gap/864e5),n=Math.floor(gap%864e5/36e5),o=Math.floor(gap%36e5/6e4),a=Math.floor(gap%6e4/1e3);document.getElementById("days").innerText=t,document.getElementById("hours").innerText=n,document.getElementById("minutes").innerText=o,document.getElementById("seconds").innerText=a}setInterval(function(){countDown()},1e3);